import React from "react";

class SendMoney extends React.Component {
  state = {
    resiver: null,
    amount: 0
  };

  componentDidMount() {
    axios
      .post("http://localhost:8000/balance/" + this.state.userName)
      .then(res => {
        console.log(res);
        const user = res.data[0];
        this.setState({ balance: user.balance });
      })
      .catch(err => {
        console.error(err);
      });
  }

  render() {
    return (
      <div>
        <div>
          <label>Receiver</label>
          <input
            onChange={e => {
              this.setState({ resiver: e.target.value });
            }}
          ></input>
          <label>Amount</label>
          <input
            onChange={e => {
              this.setState({ amount: e.target.value });
            }}
          ></input>
          <button type="button">Send</button>
        </div>
        <div>
          <label></label>
        </div>
      </div>
    );
  }
}

export default SendMoney;
