import React from "react";
import axios from "axios";

class UserUI extends React.Component {
  componentDidMount() {
    axios
      .get("http://localhost:8000/balance/" + this.props.userName)
      .then(res => {
        console.log(res);
        // this.setState({ balance: res.data.balance });
        this.props.balanceChanged(res.data.balance);
      })
      .catch(err => {
        console.error(err);
      });
  }

  render() {
    return (
      <div>
        <nav className="navbar navbar-default navbar-fixed-top">
          <div className="container">
            <h1 className="navbar-brand">User: {this.props.userName}</h1>
            <h1 className="navbar-brand">Balance: {this.props.balance}</h1>
          </div>
        </nav>
      </div>
    );
  }
}

export default UserUI;
