import React from "react";

class UserUI extends React.Component {
  state = { userName: this.props.userName, balance: "..." };

  componentDidMount() {
    fetch("http://localhost:3000/balance/" + this.state.userName)
      .then(res => res.json())
      .then(
        result => {
          this.setState({
            balance: result.balance
          });
        },
        // Note: it's important to handle errors here
        // instead of a catch() block so that we don't swallow
        // exceptions from actual bugs in components.
        error => {}
      );
  }

  render() {
    return (
      <div>
        <nav class="navbar navbar-default navbar-fixed-top">
          <div class="container">
            <h1 class="navbar-brand">User: {this.state.userName}</h1>
            <h1 class="navbar-brand">Balance: {this.state.balance}</h1>
          </div>
        </nav>
      </div>
    );
  }
}

export default UserUI;
