import React from "react";

class UserUI extends React.Component {
  state = { userName: this.props.userName, balance: "..." };

  componentDidMount() {
    fetch("http://localhost:8000/balance/" + this.state.userName)
      .then(res => res.json())
      .then(
        result => {
          this.setState({
            balance: result.balance
          });
        },
        error => {
          console.log(
            "could not found balance for user " + this.state.userName
          );
        }
      );
  }

  render() {
    return (
      <div>
        <nav class="navbar navbar-default navbar-fixed-top">
          <div class="container">
            <h1 class="navbar-brand">User: {this.state.userName}</h1>
            <h1 class="navbar-brand">Balance: {this.state.balance}</h1>
          </div>
        </nav>
      </div>
    );
  }
}

export default UserUI;
