import React from "react";
import axios from "axios";

class UserUI extends React.Component {
  state = { userName: this.props.userName, balance: "..." };

  componentDidMount() {
    axios
      .get("http://localhost:8000/balance/" + this.state.userName)
      .then(res => {
        console.log(res);
        const user = res.data[0];
        this.setState({ balance: user.balance });
      })
      .catch(err => {
        console.error(err);
      });
  }

  render() {
    return (
      <div>
        <nav className="navbar navbar-default navbar-fixed-top">
          <div className="container">
            <h1 className="navbar-brand">User: {this.state.userName}</h1>
            <h1 className="navbar-brand">Balance: {this.state.balance}</h1>
          </div>
        </nav>
      </div>
    );
  }
}

export default UserUI;
